css:
@font-face {
    font-family: Calibri;
    src: url('/files/Calibri.ttf');
}

.print-format {
    font-family: Calibri;
    font-size: 12pt;
}

p {
    width:99%;
}

.ql-editor {
    font-family: Calibri !important;
    font-size: 12pt !important;
    line-height: 1.2 !important;
}

.header-address {
    float: right; 
    text-align: right; 
    width:9cm;
    line-height: 1.1;
}

p.standard-para {
    margin-top: 1em;
}

div.alignment-heading {
    margin-top: 1em;
    float: left;
    display: inline-block;
    vertical-align: text-top;
    width:230px !important;
}

div.alignment-value {
    margin-top: 1em;
    display: inline-block;
    width:300px !important;
}

div.alignment-cost-heading {
    text-align: right;
    display: inline-block;
    width:400px !important;
    line-height: 1.2;
}

div.alignment-cost-value {
    text-align: right;
    display: inline-block;
    width:200px !important;
}

div.section-heading {
    margin-top: 1em;
    font-style: italic;
}

div.standard-heading {
    margin-top: 1em;
}

div.first-line-date {
    margin-top: 4em;
    margin-bottom: 2em;
    display: inline-block;
    width: 400px;
}

div.first-line-quote {
    margin-top: 4em;
    margin-bottom: 2em;
    display: inline-block;
    float: right;
}

div.address {
    margin-bottom: 3em;
    line-height: 1.1;
}

div.signature-top {
    margin-top:1em;
}

div.signature {
    display: table-cell;
    vertical-align: middle;
}

div.customer-sig {
    margin-top: 2.5em;
}

hr.solid {
    border-top: 1px solid black;
}

table {
   width: 100%;
   border: none;
}

td {
   width: 100%;
}
