css:
body {
    font-family: Arial, Helvetica, sans-serif;
}

div.standard-para {
    margin-top: 1em;
}

div.alignment-heading {
    margin-top: 1em;
    display: inline-block;
    width:160px !important;
}

div.alignment-value {
    display: inline-block;
    width:300px !important;
}

div.alignment-cost-heading {
    text-align: right;
    display: inline-block;
    width:400px !important;
}

div.alignment-cost-value {
    text-align: right;
    display: inline-block;
    width:100px !important;
}

div.section-heading {
    margin-top: 1em;
    font-style: italic;
}

div.standard-heading {
    margin-top: 1em;
}

div.first-line-date {
    margin-top: 5em;
    margin-bottom: 3em;
    display: inline-block;
}

div.first-line-quote {
    margin-top: 5em;
    margin-bottom: 3em;
    display: inline-block;
    float: right;
}

div.address {
    margin-bottom: 3em;
}

div.signature-top {
    margin-top:1.5em;
}

div.signature {
    display: table-cell;
    vertical-align: middle;
}

div.customer-sig {
    margin-top: 3em;
}

hr.dotted {
    border-top: 3px dotted #bbb;
}
